-- Table definitions

CREATE TABLE media_type (
    media_type_id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    media_type_name TEXT UNIQUE NOT NULL
);

CREATE TABLE rating (
    rating_id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    rating_name TEXT UNIQUE NOT NULL
);

CREATE TABLE country (
    country_id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    country_name TEXT UNIQUE NOT NULL
);

CREATE TABLE director (
    director_id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    director_name TEXT UNIQUE NOT NULL
);

CREATE TABLE genre (
	genre_id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
	genre_name TEXT UNIQUE NOT NULL
);

CREATE TABLE media (
    media_id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    title text,
    date_added date,
    release_year integer,
    description text,
    media_type_id INT NOT NULL,
	rating_id INT NOT NULL,
    FOREIGN KEY (media_type_id) REFERENCES media_type(media_type_id),
	FOREIGN KEY (rating_id) REFERENCES rating(rating_id)
);

CREATE TABLE media_director_link (
    media_director_link_id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    media_id INT NOT NULL,
    director_id INT NOT NULL,
    FOREIGN KEY (media_id) REFERENCES media(media_id),
    FOREIGN KEY (director_id) REFERENCES director(director_id)
);

CREATE TABLE media_country_link (
    media_country_link_id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    media_id INT NOT NULL,
    country_id INT NOT NULL,
    FOREIGN KEY (media_id) REFERENCES media(media_id),
    FOREIGN KEY (country_id) REFERENCES country(country_id)
);

CREATE TABLE media_genre_link (
    media_genre_link_id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    media_id INT NOT NULL,
    genre_id INT NOT NULL,
    FOREIGN KEY (media_id) REFERENCES media(media_id),
    FOREIGN KEY (genre_id) REFERENCES genre(genre_id)
);

